
$(function () {
        $('#country').change(function() {
            var val = $(this).val();
            if(val === "Nepal") {

                var provinceno = document.getElementById("provinceno");
                provinceno.value = "";

                var wardno = document.getElementById("wardno");
                wardno.value= "";

                var streetaddress = document.getElementById("streetaddress");
                streetaddress.value= "";

                $("#nepaldetailinfoshow").show();
                
            }
            else {
                select_box_zone = document.getElementById("zone");
                select_box_zone.selectedIndex = 0;

                select_box_district = document.getElementById("district");
                select_box_district.selectedIndex = 0;

                select_box_provinceno = document.getElementById("provinceno");
                select_box_provinceno.selectedIndex = 0;

                select_box_wardno = document.getElementById("wardno");
                select_box_wardno.selectedIndex = 0;

                select_box_village = document.getElementById("village");
                select_box_village.selectedIndex = 0;

                document.getElementById("streetaddress").value = '';
                $("#nepaldetailinfoshow").hide();
            }
        });
    });
	
	
	
	
